package in.mkpits.array;

public class DemoOfMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		DemoOfMath pk = new DemoOfMath();
		pk.MathsDemo();
		
	}

	public void MathsDemo() {
		System.out.println(Math.subtractExact(24, 6));
		System.out.println(Math.addExact(2, 6));
		System.out.println(Math.abs(21.6));
		System.out.println(Math.decrementExact(6));
		System.out.println(Math.incrementExact(48));
		System.out.println(Math.floorDiv(2, 6));
		System.out.println(Math.multiplyExact(24, 12));
		System.out.println(Math.max(2, 8));
		System.out.println(Math.min(7, 3));
		System.out.println(Math.PI);
		System.out.println(Math.sqrt(6));
		
		
	}

}
