package in.mkpits.basic;

public class ImplicitPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a= 55, b= 55;
       byte p=55 ,i=66;
       long s= (a+b)+(p+i);
       System.out.println(s);
       
       long f= 55l, g= 66l;
       int h = 7 , j = 4; 
       byte k= (byte)((f+g)+(h+j));
       System.out.println(k);
       
       int z= 8 , l= 9;
        char ch = 'U';
        byte o = (byte)(ch);
        byte y = (byte)(z+l);
        System.out.println(o+" "+y);
        
        
       
	}

}
